import React, { useEffect} from "react";
import {getIdVideogames} from '../actions/index';
import { Link} from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";

export default function DetailVideogame(props){

const dispatch=useDispatch();
useEffect(()=>{
dispatch(getIdVideogames(props.match.params.id));
},[dispatch])
const myVideogame=useSelector((state)=>state.detail)
return (
    <div className={style.sameSpot}>
    <div className={style.detail}>
        
        <div><Link to="/home"><button className={style.button1}>Back to home</button></Link></div>
        {
            myVideogame.length>0 ?
            <div className={style.position}>
                <div className={style.text}>
                <h1>{myVideogame[0].name.charAt(0).toUpperCase() + myVideogame[0].name.slice(1)}</h1>
                <img alt='' width='300px' height="300px" src={myVideogame[0].img? myVideogame[0].img: myVideogame[0].background_image} />
                </div>
                <div>
                <div >
                <label className={style.text2}>Description</label><h1>{myVideogame[0].description}</h1>
                </div>
                <div>
                <label className={style.text2}>Genres</label><h1>{myVideogame[0].genres.map(elem=>elem.name).join(" ")}</h1>
                </div>
                <div>
                <label className={style.text2}>Platforms</label><h1>{myVideogame[0].platforms}</h1>
                </div>
                <div>
                <label className={style.text2}>Rating</label><h1 value={myVideogame[0].rating}/>
                </div>
                <div>
                <label className={style.text2}>Released Date</label><h1 value={myVideogame[0].released}/>
                </div>
                </div>              
            </div>:<div className={style.img2}>
            <img  src="https://media.tenor.com/74l5y1hUdtwAAAAj/pokemon.gif"
            width="250px" height="250px" />
            <p>Loading...</p>
           </div>
        }
        </div>
    </div>
)
}
