import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getVideogames, filterVideogamesByGenres, orderByRating, filterCreated, orderByName } from "../actions";
import Card from "./Card";
import Paginacion from "./Paginado";
import { Link } from "react-router-dom";
import SearchBar from "./SearchBar";

export default function Home(){
    
    const dispatch =useDispatch();
    const allVideogames = useSelector(state=>state.videogames)
    const [orden,setOrden]=useState('')
    const [currentPage, setCurrentPage]=useState(1)
    const [videogamesPage, setVideogamesPage]=useState(15)
    const indexOfLastVideogame=currentPage * videogamesPage
    const indexOfFirstVideogame=indexOfLastVideogame - videogamesPage
    const currentVideogames= allVideogames.slice(indexOfFirstVideogame, indexOfLastVideogame);
    const [name, setName]=useState("")
    const [searchError, setSearchError] = useState(false); 
    const [selectedSort, setSelectedSort] = useState(""); // Estado del filtro de ordenamiento
    const [selectedFilterType, setSelectedFilterType] = useState("All"); // Estado del filtro de tipos
    const [selectedFilterCreated, setSelectedFilterCreated] = useState("All"); // Estado del filtro de creación
    const [loading, setLoading] = useState(true);
    const paginado =(pageNumber)=>{
        setCurrentPage(pageNumber)
    }
    useEffect(()=>{
        setLoading(true);
        dispatch(getVideogames())
        .then(() => setLoading(false));
    },[dispatch])

    const handleClick =(e)=>{
        e.preventDefault();
        dispatch(getVideogames())
    };
    const handleSort =(e)=>{
        e.preventDefault();
        dispatch(orderByName(e.target.value))
        setCurrentPage(1);
        setOrden(`Ordenado ${e.target.value}`)
    }
    const handleSortRating =(e)=>{
        e.preventDefault();
        dispatch(orderByRating(e.target.value))
        setCurrentPage(1);
        setOrden(`Ordenado ${e.target.value}`)
    }
    const handleFilterGenres=(e)=>{
        dispatch(filterVideogamesByGenres(e.target.value))
    };
    const handleFilterCreated=(e)=>{
        dispatch(filterCreated(e.target.value))
    };
    return (
        <div>
        <Link to='/videogame'>Add videogames</Link>
        <div >
        <h1>Videogames</h1>
        <div>
        <button onClick={e=>{handleClick(e)}}>Borrar filtros</button>
        </div>
        <select onChange={e=>handleSort(e)}>
             <option value='All'>---</option>
            <option value="asc" >A-Z</option>
            <option value="desc">Z-A</option>
        </select>
        <select onChange={e=>handleSortRating(e)}>
            <option value='All'>---</option>
            <option value="asc rating">- Rating</option>
            <option value="desc rating">+ Rating</option>
        </select>
        <select onChange={e=>handleFilterCreated(e)}>
            <option value='All'>---</option>
            <option value="created">Creados</option>
            <option value="api">Existentes</option>
        </select>
        <select onChange={e=>handleFilterGenres(e)}>
            <option value="All">---</option>
            <option value="Action">Action</option>
            <option value="Indie">Indie</option>
            <option value="Adventure">Adventure</option>
            <option value="RPG">RPG</option>
            <option value="Strategy">Strategy</option>
            <option value="Shooter">Shooter</option>
            <option value="Casual">Casual</option>
            <option value="Simulation">Simulation</option>
            <option value="Puzzle">Puzzle</option>
            <option value="Arcade">Arcade</option>
            <option value="Platformer">Platformer</option>
            <option value="Racing">Racing</option>
            <option value="Massively Multiplayer">Massively Multiplayer</option>
            <option value="Sports">Sports</option>
            <option value="Fighting">Fighting</option>
            <option value="Family">Family</option>
            <option value="Board Games">Board Games</option>
            <option value="Educational">Educational</option>
            <option value="Card">Card</option>
        </select>
        <SearchBar/>
        <Paginacion videogamesPage={videogamesPage}
        allVideogames={allVideogames.length}
        paginado ={paginado}/>
            {currentVideogames?.map((videogame)=>{
                return (
                <div >
                <Link to={"/home/" + videogame.id}>
                <Card name={videogame.name} genres={videogame.genres} background_image={videogame.background_image} key={videogame.id}/>
                </Link>
                </div>
                )
            })}
        </div>
        </div>
    )

}
