import React, {useState, useEffect} from "react";
import {getGenres, postVideogames} from '../actions/index';
import { Link, useHistory } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";

function validate(input){
    let errors={};
    if(!input.name){
        errors.name="Se requiere un nombre"
    } else if (!input.image){
        errors.image="La imagen debe ser un link";
    }
    return errors;
}
export default function CreateVideogame(){
    const dispatch=useDispatch()
    const history=useHistory()
    const genres=useSelector((state)=>state.genres)
    const [errors, setErrors]=useState({})
    const [input, setInput]=useState({
        name:"", 
        description:"",
        platforms:"", 
        background_image:"",
        released:"",
        rating:"",  
        genres:[]
    })
    const handleChange=(e)=>{
        setInput({
            ...input,
            [e.target.name]:e.target.value
        })
        setErrors(validate({
            ...input,
            [e.target.name]:e.target.value
        }))
    };
    const handleSelect=(e)=>{
        if(e.target.checked){
            setInput({
                ...input, 
                genres:[...input.genres, 
                    e.target.value]
        })
        }
    }
    const handleSubmit=(e)=>{
        e.preventDefault()
        dispatch(postVideogames(input))
        alert("Videogame created")
        setInput({
            name:"", 
            description:"",
            platforms:"", 
            background_image:"",
            released:"",
            rating:"",  
            genres:[]
        })
        history.push("/home")
    }
    useEffect(()=>{
        dispatch(getGenres())
    },[]);
    return (
        <div>
            <Link to='/home'><button>Back to home</button></Link>
            <h1>Create your videogame</h1>
            <form onSubmit={(e)=>handleSubmit(e)}>
                <div>
                <label>Name</label>
                <input type='text' value={input.name} name='name' onChange={(e)=>handleChange(e)}></input>
                {errors.name &&(
                    <p className="error">{errors.name}</p>
                )}
                </div>
                <div>
                <label>Description</label>
                <input type='text' value={input.description} name='description' onChange={(e)=>handleChange(e)}></input>
                </div>
                <div>
                <label>Platforms</label>
                <input type='text' value={input.platforms} name='platforms' onChange={(e)=>handleChange(e)}></input>
                </div>
                <div>
                <label>Image</label>
                <input type='text' value={input.background_image} name='background_image' onChange={(e)=>handleChange(e)}></input>
                {errors.background_image &&(
                    <p className="error">{errors.background_image}</p>
                )}
                </div>
                <div>
                <label>Released date</label>
                <input type='text' value={input.released} name='released' onChange={(e)=>handleChange(e)}></input>
                </div>
                <div>
                <label>Rating</label>
                <input type='text' value={input.rating} name='rating' onChange={(e)=>handleChange(e)}></input>
                </div>
                <div>
                <select onChange={(e)=>handleSelect(e)}> 
                {genres.map((elem)=> {return <option value={elem.name}>{elem.name}</option>})}
                </select>
                <ul><li>{input.genres.map(elem=>elem + " ,")}</li></ul>
                </div>
                <button type="submit">Create pokemon</button>
            </form>
        </div>
    )
}
